
public class EvenOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//int num;
		
		for(int num=1; num<=100; num++)
		
		if(num%2==0) {
			
			System.out.println(num+ "is even");
		}
		
		else {
			
			System.out.println(num+ "is odd");
			
			
		}

	}

}
