
public class Oprators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 22;
		
		int b=  12;
		
		
		System.out.println(a+b);
		System.out.println(a*b);
		System.out.println(a%b);
		System.out.println(a>b && a<b);
		System.out.println(a!=b && a>b);
		System.out.println(a/b);
		System.out.println(a!=b || a>b);
		
		
		
	}

}