
public class starPattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int total_row=4;
		
		for(int row=1; row<=4; row++) {
			
			for(int st=1; st<=total_row+row; st++) {
				
				System.out.print("*");
				
			}
			System.out.println();
		}
		

	}

}
